import pygamefrom pygame.locals import *import Game as Gameimport sysdef main():	pygame.init()	screensize = (640, 380)	screen = pygame.display.set_mode(screensize)	pygame.display.set_caption('Pong!')	clock = pygame.time.Clock()	pos = pygame.mouse.get_pos()	button = MakeButton((screensize[0]*0.5) - 50, (screensize[1]*0.5) - 25, 100, 50, screensize, screen)	exitButton = MakeButton(button.centerx - (button.centerx - button.left), button.centery + 100, 100, 50, screensize, screen)	twoButton = MakeButton(button.centerx - (button.centerx - button.left), button.centery + 40, 100, 50, screensize, screen)	font = pygame.font.SysFont('Arial', 25)	otherFont = pygame.font.SysFont('Arial', 20)	titleFont = pygame.font.SysFont('Arial', 50)	while True:		clock.tick(64)		pos = (0,0)		for event in pygame.event.get():			if event.type == pygame.MOUSEBUTTONDOWN and event.button == 1:				pos = pygame.mouse.get_pos()			if button.collidepoint((pos[0], pos[1])):				Game.OnePlayer_StartUp()			if twoButton.collidepoint((pos[0], pos[1])):				Game.TwoPlayer_StartUp()			if exitButton.collidepoint((pos[0], pos[1])):				pygame.quit()				sys.exit()		screen.fill((250,250,250))			pygame.draw.rect(screen, (100,100,100), button, 0)		pygame.draw.rect(screen, (0,0,0), button, 1)		pygame.draw.rect(screen, (100,100,100), twoButton,  0)		pygame.draw.rect(screen, (0,0,0), twoButton, 1)		pygame.draw.rect(screen, (100,100,100), exitButton, 0)		pygame.draw.rect(screen, (0,0,0), exitButton, 1)		screen.blit(otherFont.render('1 Player', True, (250,250,250)), (button.center[0] - 30, button.center[1] - 15))		screen.blit(otherFont.render('2 Player', True, (250,250,250)), (twoButton.center[0] - 30, twoButton.center[1] - 15))		screen.blit(titleFont.render('Pong', True, (0,0,0)), (button.center[0] - 50, button.center[1] - 200))		screen.blit(font.render('Exit', True, (250,250,250)), (exitButton.centerx - 22, exitButton.centery - 15))		pygame.display.flip()def MakeButton(left, top, width, height, screensize, screen):	button = pygame.Rect(left, top, width, height)	return buttonmain()